    <script>
        // Check for a theme preference in localStorage
        const theme = localStorage.getItem('theme');

        // If a theme is set, apply it; otherwise, set to light mode
        if (theme) {
            document.documentElement.classList.add(theme);
        } else {
            document.documentElement.classList.remove('dark'); // Set to light mode by default
            localStorage.setItem('theme', 'light');
        }
    </script>
